var createError = require('http-errors');
var express = require('express');
var path = require('path');
var cookieParser = require('cookie-parser');
var logger = require('morgan');
const mongoose = require('mongoose');
var indexRouter = require('./routes/index');
var usersRouter = require('./routes/users');
//Get Employee Model
var Employee = require('./model/employee-model');
var app = express();

// view engine setup
app.set('views', path.join(__dirname, 'views'));
app.set('view engine', 'ejs');
app.use(logger('dev'));
app.use(express.json());
app.use(express.urlencoded({
  extended: false
}));
app.use(cookieParser());
app.use(express.static(path.join(__dirname, 'public')));

//Connextion with Database (MongoDB)
mongoose.connect('mongodb://127.0.0.1:27017/Employee', {
  useNewUrlParser: true,
  useUnifiedTopology: true
}).then(() => console.log("connexion reussi")).catch(() => console.log("echec de connexion"));

//create a new employee
app.post('/create', async (req, res) => {
  try {
    const {
      id,
      lastName,
      firstName,
      department
    } = req.body;
    const dateCreated = new Date();
    const employee = new Employee({
      id,
      lastName,
      firstName,
      department,
      dateCreated
    });
    await employee.save();
    res.status(201).json(employee);
  } catch (error) {
    res.status(500).json({
      error: 'Error in created the employee'
    });
  }
});

//get list of employees
app.get('/employees', (req, res) => {
  Employee.find().then(employees => {
    res.status(200).json(employees);
    //res.render('index',{'employees':employees})
  }).catch(err => {
    res.status(500).json({
      error: 'No employee found'
    });
  });
});

//get employee with filter
app.get('/employeesBydate', (req, res) => {
  const dateToSearch = new Date("2021-01-05");
  Employee.find({
    dateCreated: dateToSearch
  }).then(employees => {
    res.status(200).json(employees);
  }).catch(err => {
    res.status(500).json({
      err: 'No employee found'
    });
  });
});

//Check-in endpoint
app.post('/check-in/:employeeId/', async (req, res) => {
  try {
    const employeeId = req.params.employeeId;
    const comment = req.body.comment;
    const checkInDate = new Date();
    const employee = await Employee.findById(employeeId);
    if (!employee) {
      res.status(404).json({
        error: 'Employee not Found'
      });
    }
    //Update checkIn and comment of the employee
    employee.checkIn = checkInDate;
    employee.comment = comment;
    await employee.save();
    res.status(201).json(employee);
  } catch (error) {
    res.status(500).json({
      error: 'Check-in failed'
    });
  }
});

//Check-out endpoint
app.post('/check-out/:employeeId/', async (req, res) => {
  try {
    const employeeId = req.params.employeeId;
    const comment = req.body.comment;
    const employee = await Employee.findById(employeeId);
    if (!employee) {
      return res.status(404).json({
        error: 'Employee not Found'
      });
    }
    if (!employee.checkIn) {
      res.status(404).json({
        error: 'Employee does not checkIn already'
      });
    }
    // get checkOut date
    const checkOutDate = new Date();
    console.log(checkOutDate);
    // get checkIn date
    const checkIn = employee.checkIn;
    console.log(checkIn);
    // calculate time diffrence between checkIn and checkOut
    const TimeDiffer = checkOutDate - checkIn;
    console.log(TimeDiffer);
    //update checkout and comment of the employee
    employee.checkOut = checkOutDate;
    employee.comment = comment;
    //add the time to database
    employee.TimeDiffer = TimeDiffer;
    // save changes in database
    await employee.save();
    res.status(201).json(employee);
  } catch (error) {
    res.status(500).json({
      error: 'Check-out failed'
    });
  }
});
app.use('/', indexRouter);
app.use('/users', usersRouter);

// catch 404 and forward to error handler
app.use(function (req, res, next) {
  next(createError(404));
});

// error handler
app.use(function (err, req, res, next) {
  // set locals, only providing error in development
  res.locals.message = err.message;
  res.locals.error = req.app.get('env') === 'development' ? err : {};

  // render the error page
  res.status(err.status || 500);
  res.render('error');
});
module.exports = app;
